import React from 'react';
import './App.css';

function App() {
  const choices = ['rock', 'paper', 'scissor']

  function computerPlay() {
    return choices[Math.floor(Math.random()*choices.length)]
  }

  function mod(a, b) {
    const c = a % b
    return (c < 0) ? c + b : c
  }

  function playRound(playerSelection, compSelection) {
    const x = choices.indexOf(playerSelection);
    const y = choices.indexOf(compSelection);
    if (x == y) {
      return "Tie";
    }
    if (mod((x - y), choices.length) < choices.length / 2) {
      return playerSelection + " wins";
    } else {
      return compSelection + " wins";
    }
  }

  const compSel = computerPlay();
  const playerSel = 'rock';
  return (
    <div className="App">
      {console.log('Player choose', playerSel, 'Comp choose', compSel, 'Result', playRound(playerSel, compSel))}
      Test
    </div>
  );
}

export default App;
