import React, { useState } from 'react';
import './App.css';

function App() {
  const [playerSel, setPlayerSel] = useState('');
  const [result, setResult] = useState('');

  const choices = ['rock', 'paper', 'scissor']

  function handleChange(event) {
    setPlayerSel(event.target.value)
  }

  function handleSubmit(e, compSel) {
    setResult(playRound(playerSel, compSel))
    e.preventDefault()
  }

  function computerPlay() {
    return choices[Math.floor(Math.random()*choices.length)]
  }

  function playRound(playerSelection, compSelection) {
    const x = choices.indexOf(playerSelection);
    const y = choices.indexOf(compSelection);
    if (x === y) {
      return "Tie";
    }

    if (x-y === 1 || x-y === -2) {
      return "Player wins with " + playerSelection;
    } else if (y-x === 1 || y-x === -2) {
      return "Comp wins with " + compSelection;
    }
  }

  const compSel = computerPlay();
  return (
    <div className="App">
      <form onSubmit={(e) => handleSubmit(e, compSel)}>
        <label>Player choose:</label>
        <div className="radio">
          <label>
            <input type="radio" value="option1" checked={playerSel === 'rock'} onChange={e => handleChange(e)}/>
            Rock
          </label>
        </div>
        <div className="radio">
          <label>
            <input type="radio" value="option2" checked={playerSel === 'paper'} onChange={e => handleChange(e)}/>
            Paper
          </label>
        </div>
        <div className="radio">
          <label>
            <input type="radio" value="option3" checked={playerSel === 'scissor'} onChange={e => handleChange(e)}/>
            Scissor
          </label>
        </div>
        <input type="submit" value="Submit" />
      </form>
      {/*<form onSubmit={(e) => handleSubmit(e, compSel)}>*/}
      {/*  <label>*/}
      {/*    Player Choose:*/}
      {/*    <input type="text" name="playerChoice" value={playerSel} onChange={e => handleChange(e)}/>*/}
      {/*  </label>*/}
      {/*  <input type="submit" value="Submit" />*/}
      {/*</form>*/}
      {result && <h4>Comp choose {compSel}</h4>}
      <h4>{result}</h4>
    </div>
  );
}

export default App;
