import React from 'react';
import './App.css';

function App() {
  const [playerSel, setPlayerSel] = useState('');
  this.state = {
    playerSel: ''
  }

  const choices = ['rock', 'paper', 'scissor']

  function handleChange(event) {
    this.setState({value: event.target.value});
  }

  function computerPlay() {
    return choices[Math.floor(Math.random()*choices.length)]
  }

  function playRound(playerSelection, compSelection) {
    const x = choices.indexOf(playerSelection);
    const y = choices.indexOf(compSelection);
    if (x === y) {
      return "Tie";
    }

    if (x-y === 1 || x-y === -2) {
      return "Player wins with " + playerSelection;
    } else if (y-x === 1 || y-x === -2) {
      return "Comp wins with " + compSelection;
    }
  }

  const compSel = computerPlay();
  return (
    <div className="App">
      <form onSubmit={playRound(this.state.playerSel, compSel)}>
        <label>
          Player Choose:
          <input type="text" name="playerChoice" value={this.state.playerSel} onChange={handleChange()}/>
        </label>
        <input type="submit" value="Submit" />
      </form>
      {/*{console.log('Player choose', playerSel, '\nComp choose', compSel, '\nResult', playRound(playerSel, compSel))}*/}
    </div>
  );
}

export default App;
